import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';

import useStyles from './styles';
import { createJob, updateJob } from '../../actions/jobs';
import StatusDropdown from './StatusDropdown/StatusDropdown';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';

const Form = ({ currentId, setCurrentId }) => {
  const [jobData, setJobData] = useState({title: '', company: '', link: '', selectedFile: '' });
  const job = useSelector((state) => (currentId ? state.jobs.find((message) => message._id === currentId) : null));
  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    if (job) setJobData(job);
  }, [job]);

  const clear = () => {
    setCurrentId(0);
    setJobData({ title: '', company: '', link: '', selectedFile: '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (currentId === 0) {
      dispatch(createJob(jobData));
      clear();
    } else {
      dispatch(updateJob(currentId, jobData));
      clear();
    }
  };

  return (
    <Paper className={classes.paper}>
      <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
        <Typography variant="h6">{currentId ? `Editing "${job.title}"` : 'Creating a Job'}</Typography>
       
        <TextField name="title" variant="outlined" label="Title" fullWidth value={jobData.title} onChange={(e) => setJobData({ ...jobData, title: e.target.value })} />
        <TextField name="company" variant="outlined" label="Company" fullWidth value={jobData.company} onChange={(e) => setJobData({ ...jobData, company: e.target.value })} />
        <TextField name="Link" variant="outlined" label="Link" fullWidth multiline rows={4} value={jobData.link} onChange={(e) => setJobData({ ...jobData, link: e.target.value })} />
        <TextField name="status" variant="outlined" label="Status" fullWidth value={jobData.status} onChange={(e) => setJobData({ ...jobData, status: e.target.value })} />
        <Select
    labelId="demo-simple-select-label"
    id="demo-simple-select"
    value={["Not Started", "In Progress", "Submitted"]}
    label="ASDFASDFAsd"
    autoWidth ="true"
    
    onChange={handleChange}
  >
    <MenuItem value={10}>Ten</MenuItem>
    <MenuItem value={20}>Twenty</MenuItem>
    <MenuItem value={30}>Thirty</MenuItem>
  </Select>
        <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setJobData({ ...jobData, selectedFile: base64 })} /></div>
        <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
        <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>
      </form>
    </Paper>
  );
};

export default Form;
