import React from 'react';
import { Link } from "react-router-dom";
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core/';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment';
import { useDispatch } from 'react-redux';
import { likePost, deleteJob } from '../../../actions/jobs';
import useStyles from './styles';

const Job = ({ job, setCurrentId }) => {
  const dispatch = useDispatch();
  const classes = useStyles();

  return (
    <Card className={classes.card}>
       <Link to={`/jobs/${job._id}`}></Link>
      <CardMedia className={classes.media} image={job.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} title={job.title} />
      <div className={classes.overlay}>
        <Typography variant="h6">{job.company}</Typography>
        <Typography variant="body2">{moment(job.createdAt).fromNow()}</Typography>
      </div>
      <div className={classes.overlay2}>
        <Button style={{ color: 'white' }} size="small" onClick={() => setCurrentId(job._id)}><MoreHorizIcon fontSize="default" /></Button>
      </div>
      <div className={classes.details}>
        <Typography variant="body2" color="textSecondary" component="h2">{job.status}</Typography>
      </div>
      <Typography className={classes.title} gutterBottom variant="h5" component="h2">{job.title}</Typography>
      <CardContent>
      <Typography  variant="body1" color="textPrimary" >{ job.location ?  `Location: ${job.location}` : ""}</Typography>
      <a href={job.link} target="_blank"> <Typography variant="body2" component="p">{job.link}</Typography></a>
      </CardContent>
      <Typography>{job.notes}</Typography>
      <CardActions className={classes.cardActions}>
        {/* <Button size="small" color="primary" onClick={() => dispatch(likePost(post._id))}><ThumbUpAltIcon fontSize="small" /> Like {post.likeCount} </Button> */}
        <Button size="small" color="primary" onClick={() => dispatch(deleteJob(job._id))}><DeleteIcon fontSize="small" /> Delete</Button>
      </CardActions>
    </Card>
  );
};

export default Job;
