import React from "react";
import {
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography,
} from "@material-ui/core/";
import DeleteIcon from "@material-ui/icons/Delete";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import moment from "moment";
import { useDispatch } from "react-redux";
import { deleteJob } from "../../../actions/jobs";
import useStyles from "./styles";

const Job = ({ job, setCurrentId }) => {
  const dispatch = useDispatch();
  const classes = useStyles();

  // Function to generate the cover letter download link
  const downloadCoverLetter = () => {
    const link = document.createElement("a");
    link.href = `data:application/pdf;base64,${job.coverLetterFile}`;
    link.download = "cover_letter.pdf";
    link.target = "_blank";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };
  return (
    <Card className={classes.card}>
      <CardMedia
        className={classes.media}
        image={
          job.selectedFile ||
          "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"
        }
        title={job.title}
      />
      <div className={classes.overlay}>
        <Typography variant="h6">{job.company}</Typography>
        <Typography variant="body2">
          {moment(job.createdAt).fromNow()}
        </Typography>
      </div>
      <div className={classes.overlay2}>
        <Button
          style={{ color: "white" }}
          size="small"
          onClick={() => setCurrentId(job._id)}
        >
          <MoreHorizIcon fontSize="default" />
        </Button>
      </div>
      <div className={classes.details}>
        <Typography variant="body2" color="textSecondary" component="h2">
          {job.status}
        </Typography>
      </div>
      <Typography
        className={classes.title}
        gutterBottom
        variant="h5"
        component="h2"
      >
        {job.title}
      </Typography>
      <CardContent>
        <Typography variant="body1" color="textPrimary">
          {job.location ? `Location: ${job.location}` : ""}
        </Typography>
        <a href={job.link} target="_blank">
          {" "}
          <Typography variant="body2" component="p">
            {job.link}
          </Typography>
        </a>
      </CardContent>
      <Typography>{job.notes}</Typography>
      <Typography>{job.coverLetterFile}</Typography>
      <CardActions className={classes.cardActions}>
        {job.coverLetterFile && (
          <div>
            <Typography>Download Cover Letter:</Typography>
            <Button
              onClick={downloadCoverLetter}
              variant="contained"
              color="primary"
            >
              Download
            </Button>
          </div>
        )}
        <Button
          size="small"
          color="primary"
          onClick={() => dispatch(deleteJob(job._id))}
        >
          <DeleteIcon fontSize="small" /> Delete
        </Button>
      </CardActions>
    </Card>
  );
};

export default Job;
